                            `format`="zto,pvalue", 
                            `visible`="(postHocCorr:bonf)"),
                        list(
                            `name`="pholm", 
                            `title`="p<sub>holm</sub>", 
                            `type`="number", 
                            `format`="zto,pvalue", 
                            `visible`="(postHocCorr:holm)")))))
            self$add(R6::R6Class(
                inherit = jmvcore::Group,
                active = list(
                    Anova = function() private$.items[["Anova"]],
                    Params = function() private$.items[["Params"]]),
                private = list(),
                public=list(
                    initialize=function(options) {
                        super$initialize(
                            options=options,
                            name="simpleEffects",
                            title="Simple Effects",
                            clearWith=list(
                    "dep",
                    "modelTerms",
                    "reml",
                    "contrasts",
                    "scaling",
                    "dep_scale",
                    "randomTerms",
                    "correlatedEffects",
                    "fixedIntercept",
                    "simpleVariable",
                    "simpleModerator",
                    "simple3way",
                    "simpleScale",
                    "cvalue",
                    "percvalue",
                    "simpleScaleLabels"))
                        self$add(jmvcore::Table$new(
                            options=options,
                            name="Anova",
                            title="Simple Effects ANOVA",
                            visible=FALSE,
                            columns=list(
                                list(
                                    `name`="threeway", 
                                    `title`="", 
                                    `visible`="(simple3way)", 
                                    `combineBelow`=TRUE),
                                list(
                                    `name`="moderator", 
                                    `title`="", 
                                    `combineBelow`=TRUE),
                                list(
                                    `name`="chisq", 
                                    `title`="X\u00B2", 
                                    `type`="number", 
                                    `visible`=FALSE),
                                list(
                                    `name`="df", 
                                    `title`="df", 
                                    `type`="number", 
                                    `visible`=FALSE),
                                list(
                                    `name`="F.ratio", 
                                    `title`="F", 
