                                    `type`="number"),
                                list(
                                    `name`="p.value", 
                                    `title`="p", 
                                    `type`="number", 
                                    `format`="zto,pvalue"))))
                        self$add(jmvcore::Table$new(
                            options=options,
                            name="Params",
                            title="Parameter Estimates for simple effects",
                            visible=FALSE,
                            columns=list(
                                list(
                                    `name`="threeway", 
                                    `title`=" ", 
                                    `visible`="(simple3way)", 
                                    `combineBelow`=TRUE),
                                list(
                                    `name`="moderator", 
                                    `title`=" ", 
                                    `combineBelow`=TRUE),
                                list(
                                    `name`="contrast", 
                                    `title`="contrast", 
                                    `type`="text"),
                                list(
                                    `name`="estimate", 
                                    `title`="Estimate", 
                                    `type`="number"),
                                list(
                                    `name`="SE", 
                                    `title`="SE", 
                                    `type`="number"),
                                list(
                                    `name`="lower.CL", 
                                    `type`="number", 
                                    `title`="Lower", 
                                    `visible`="(showParamsCI)"),
                                list(
                                    `name`="upper.CL", 
                                    `type`="number", 
                                    `title`="Upper", 
                                    `visible`="(showParamsCI)"),
                                list(
                                    `name`="df", 
                                    `title`="df", 
                                    `type`="number"),
                                list(
                                    `name`="t.ratio", 
                                    `title`="t", 
                                    `type`="number"),
                                list(
                                    `name`="z.ratio", 
                                    `title`="z", 
                                    `type`="number"),
                                list(
                                    `name`="p.value", 
                                    `title`="p", 
                                    `type`="number", 
                                    `format`="zto,pvalue"))))}))$new(options=options))
            self$add(jmvcore::Array$new(
                options=options,
                name="emeansTables",
                title="Estimated Marginal Means",
                visible="(eDesc)",
                clearWith=list(
                    "dep",
                    "modelTerms",
                    "reml",
                    "contrasts",
                    "scaling",
                    "dep_scale",
                    "simpleScaleLabels",
                    "randomTerms",
                    "correlatedEffects",
                    "fixedIntercept",
                    "ciWidth"),
                template=jmvcore::Table$new(
                    options=options,
                    title="$key",
                    clearWith=list(),
                    columns=list(
                        list(
                            `name`="emmean", 
