            self$add(jmvcore::Image$new(
                options=options,
                name="descPlot",
                title="Effects Plots",
                visible="(plotHAxis)",
                width=500,
                height=300,
                renderFun=".descPlot",
                clearWith=list(
                    "dep",
                    "cluster",
                    "plotHAxis",
                    "plotSepLines",
