                    "simpleScaleLabels",
                    "plotRandomEffects",
                    "randomTerms",
                    "modelTerms",
                    "percvalue",
                    "cvalue")))
            self$add(jmvcore::Array$new(
                options=options,
                name="descPlots",
                title="Results Plots",
                visible="(plotSepPlots)",
                template=jmvcore::Image$new(
                    options=options,
                    title="$key",
                    renderFun=".descPlot",
                    width=500,
                    height=300,
                    clearWith=list(
                        "dep",
                        "plotHAxis",
                        "plotSepLines",
                        "plotSepPlots",
                        "plotError",
                        "ciWidth",
                        "scaling",
                        "dep_scale",
                        "modelTerms",
                        "fixedIntercept",
                        "simpleScale",
                        "simpleScaleLabels",
                        "plotDvScale",
                        "plotRandomEffects",
                        "plotRaw",
                        "randomTerms",
                        "percvalue",
                        "cvalue"))))
            self$add(jmvcore::Html$new(
                options=options,
                name="plotnotes",
                visible=FALSE))
            self$add(R6::R6Class(
                inherit = jmvcore::Group,
                active = list(
                    normTest = function() private$.items[["normTest"]],
                    qq = function() private$.items[["qq"]],
                    normPlot = function() private$.items[["normPlot"]],
                    residPlot = function() private$.items[["residPlot"]],
                    clusterBoxplot = function() private$.items[["clusterBoxplot"]],
                    randHist = function() private$.items[["randHist"]]),
                private = list(),
                public=list(
                    initialize=function(options) {
                        super$initialize(
                            options=options,
                            name="assumptions",
                            title="Assumption Checks")
                        self$add(jmvcore::Table$new(
                            options=options,
                            name="normTest",
                            title="Test for Normality of residuals",
                            visible="(normTest)",
                            rows=2,
                            columns=list(
                                list(
                                    `name`="test", 
                                    `title`="Test", 
                                    `type`="number"),
                                list(
